import AuthForm from '../components/AuthForm';
import { json } from 'react-router-dom';

function AuthenticationPage() {
  return <AuthForm />;
}

export default AuthenticationPage;

//AuthForm already uses the form component provided by React router, so can use React's router's data fetching and submission features for sending and getting data. We have to add an action that is triggered when this form is submitted.
//Use request object to access formData submitted
export async function action({request}) {
  //Can't directly access query parameters through useSearchParams() as not in component 
  const searchParams = new URL(request.url).searchParams
  const mode = searchParams.get('mode') || 'login'

  if(mode !== 'login' && mode !== 'signup'){
    throw json({message: 'Unsupported mode'}, {status: 422})
  }

  const data = await request.formData()
  const authData = {
    email: data.get('email'),
    password: data.get('password')
  }
  fetch()
}
