import EventForm from "../EventForm"
import { useLoaderData } from "react-router-dom"

export default function EditEventPage() {
    //Using loader data, it searches for closest loader data and at highest level for which it was loaded, here it is 'edit' route. To get from 'detail' page, we use routeLoaderData
    const detail = useLoaderData().event
    return <EventForm event={detail}/>
}
